namespace Mirasvit\CatalogLabel\Model;

use Magento\Framework\Model\AbstractModel;
use Mirasvit\CatalogLabel\Api\Data\NewProductsInterface;

class NewProducts extends AbstractModel implements NewProductsInterface
{
    /**
     * @return void
     */
    protected function _construct()
    {
        $this->_init(\Mirasvit\CatalogLabel\Model\ResourceModel\NewProducts::class);
    }

    /**
     * {@inheritdoc}
     */
    public function getId()
    {
        return $this->getData(self::ID);
    }

    /**
     * {@inheritdoc}
     */
    public function getProductId()
    {
        return $this->getData(self::PRODUCT_ID);
    }

    /**
     * {@inheritdoc}
     */
    public function setProductId($value)
    {
        return $this->setData(self::PRODUCT_ID, $value);
    }
}